body.script:
function muda_tamanho_barra_busca(){
    var tamanho_diferenca = 60;
    $('#campo_busca').focus(function() {
      $('#campo_busca').animate({width:($(this).width()+tamanho_diferenca)+'px'}, 'slow');
    });
    $('#campo_busca').blur(function() {
      $('#campo_busca').animate({width:($(this).width()-tamanho_diferenca)+'px'}, 'slow');
    });
}
function elementToButton(element){
    if(typeof element !== 'undefined'){
      if(element != 'd'){
        element.addClass('btn');
        element.addClass('btn-small');
        element.css('textDecoration', 'none');
      }
    }
}
function linkToButton(classe){
  var reply = $(classe);
  if(reply instanceof Array){
    for(r in reply){
      elementToButton(r);
    }
  }
  else{
    elementToButton(reply);
  }
}
$(document).ready(function() {
  muda_tamanho_barra_busca();
  linkToButton('.comment .continue a');
  linkToButton('.comment .comment-actions.secondary-text a');
});
